@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');

*{
    font-family: 'Roboto', sans-serif;
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

div.crud-div{
    margin: 20px auto;
    padding: 30px;
    text-align: center;
    background-color: #e8e8e8;
    width: 95%;
    max-width: 400px;
    border-radius: 12px;

    input{
        padding: 5px;
        margin: 10px;
        border-radius: 4px;
        border: none;

        &:focus{
            outline: none;
            border: 2px solid rgba(25, 174, 255, 30%);
        }
    }

    label.input-label{
        display: block;
        margin-top: 20px;
    }

    button{ 
        cursor: pointer;
        padding: 8px 25px;
        margin: 10px auto;
        display: block;
        border: none;
        border-radius: 5px;
        background-color: rgb(6, 255, 234);
        color: rgba(0, 0, 0, 0.692);
        font-weight: bold;
        font-size: 10pt;

    }
    div.auth-text {
        padding: 30px;
        width: 95%;
        margin: 25px auto;
        max-width: 500px;
        background-color: #e8e8e8;
        border-radius: 14px;

        button {
            display: block;
            background-color: red;
            color: white;
            padding: 10px;
            border-radius: 7px;
            border: none;
            cursor: pointer;
            margin: 25px 0 0 0;
            width: 100%;
        }
    }
}


div.home-div {
    div.button-container {
        padding: 30px;
        width: 95%;
        margin: 40px auto;
        max-width: 500px;
        display: flex;
        flex-direction: row;
        background-color: #e8e8e8;
        border-radius: 14px;
        justify-content: space-around;

        button {
        cursor: pointer;
        padding: 8px 25px;
        margin: 10px auto;
        display: block;
        border: none;
        border-radius: 5px;
        background-color: rgb(6, 255, 234);
        color: rgba(0, 0, 0, 0.692);
        font-weight: bold;
        font-size: 10pt;
        }

        
    }

    
}
